$(document).ready(function(){setInterval(get_shouts,600000);$("#ishout").submit(function(){shout_this($("#message").val());$("#message").val("");return false})});function shout_this(a){$.ajax({type:"POST",url:"/shout/",data:{message:a},success:function(b){get_shouts()}})}function get_shouts(){$.ajax({type:"POST",url:"/shout/list/",success:function(a){$("#shoutbox").html(a)}})};
